package corejavaclass;

public class CharacterConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch ='A';
		int value= ch+32;
		System.out.println("Small letter Character :"+(char)value);
		
		char ch1= 'f';
		int value2 = ch1-32;
		System.out.println("Capital letter Character :"+(char)value2);
	}

}
